mapper.select:
SELECT A.*, B.nickNM as writer, B.iuser FROM cmt_table A
INNER JOIN user_table B
ON A.iuser=B.iuser
WHERE iboard=${iboard}
ORDER BY A.regdt DESC;

mapper.update:
update post_table set cntFav=${selCntFav}

    select count(iboard) from post_fav
    where iboard=${iboard}

where iboard=${iboard}
